<?php/** * Single view event meta box * * Hooked into single_event_listing_start priority 20 * * @since  1.14.0 */global $post;do_action( 'single_event_listing_meta_before' );?><section class="meta">    <div class="col-md-12">          <div class="event-title">        <?php the_title();?>     </div>          <?php do_action( 'single_event_listing_meta_start' ); ?>                <div class="form-group meta-text" >		  	        <span><?php display_event_type($post); ?></span> 	            <big>|</big>	    	        <span><?php echo display_organizer_name() ?></span>	               <big>|</big>            <span class="event-location" itemprop="eventLocation"><?php echo display_event_location(); ?></span>	            <big>|</big>			                 <span class="date-posted" itemprop="datePosted"><date><?php printf( __( 'Updated %s ago', 'wp-event-manager' ), human_time_diff( get_the_modified_time( 'U' ), current_time( 'timestamp' ) ) ); ?></date></span>            <?php if ( is_event_cancelled() ) : ?>              <big>|</big>              <span class="event-cancelled"><?php _e( 'This event has been cancelled', 'wp-event-manager' ); ?></span>	                           <?php elseif ( ! attendees_can_apply() && 'preview' !== $post->post_status ) : ?>		                      <big>|</big>		       <span class="listing-expired"><?php _e( 'Registrations have closed', 'wp-event-manager' ); ?></span>	        <?php endif; ?>        </div>         <?php do_action( 'single_event_listing_meta_end' ); ?>  </div> <div style="clear:both;"></div> </section><?php do_action( 'single_event_listing_meta_after' ); ?>