<!-- Events Display Widget--><a href="<?php the_permalink(); ?>"> <div class="event-widget">     <div class="event-img"><?php display_event_banner(); ?></div>               <div class="event-title">       <h3><?php echo esc_html( get_the_title() ); ?></h3>    </div>    <div class="event-start-date"><?php $date_format = WP_Event_Manager_Date_Time::get_event_manager_view_date_format();      echo date_i18n( $date_format, strtotime(get_event_start_date()) );?></div>       <div class="event-location">    <?php if(get_event_location()=='Anywhere'): echo __('Online Event','wp-event-manager'); else:  display_event_location(false); endif; ?>    </div>    <div class="box-footer">      <?php if ( get_option( 'event_manager_enable_event_types' ) ) {  ?>       <div class="event-type"> <div><?php display_event_type(); ?></div> </div>      <?php } ?>                <div class="event-ticket"><?php echo '#'.get_event_ticket_option(); ?></div>    </div>  </div> </a>  