<?php if ( is_user_logged_in() ) : ?>	<fieldset>		<label><?php _e( 'Your account', 'wp-event-manager' ); ?></label>		<div class="field account-sign-in">			<?php				$user = wp_get_current_user();				printf( __( 'You are currently signed in as <strong>%s</strong>.', 'wp-event-manager' ), $user->user_login );    		?>			<a class="button" href="<?php echo apply_filters( 'submit_event_form_logout_url', wp_logout_url( get_permalink() ) ); ?>"><?php _e( 'Sign out', 'wp-event-manager' ); ?></a>		</div>	</fieldset>	<?php else :	$account_required             = event_manager_user_requires_account();	$registration_enabled         = event_manager_enable_registration();	$generate_username_from_email = event_manager_generate_username_from_email();	?>	<fieldset>		<label><?php _e( 'Have an account?', 'wp-event-manager' ); ?></label>		<div class="field account-sign-in">			<a class="button" href="<?php echo apply_filters( 'submit_event_form_login_url', wp_login_url( get_permalink() ) ); ?>"><?php _e( 'Sign in', 'wp-event-manager' ); ?></a>			<?php if ( $registration_enabled ) : ?>				<?php printf( __( 'If you don&rsquo;t have an account with us, just enter your email address and create a new one.  You will receive your password shortly in your email.', 'wp-event-manager' ), $account_required ? '' : __( 'optionally', 'wp-event-manager' ) . ' ' ); ?>			<?php elseif ( $account_required ) : ?>				<?php echo apply_filters( 'submit_event_form_login_required_message',  __('You must sign in to create a new listing.', 'wp-event-manager' ) ); ?>			<?php endif; ?>		</div>	</fieldset>	<?php if ( $registration_enabled ) : ?>		<?php if ( ! $generate_username_from_email ) : ?>			<fieldset>				<label><?php _e( 'Username', 'wp-event-manager' ); ?> <?php echo apply_filters( 'submit_event_form_required_label', ( ! $account_required ) ? ' <small>' . __( '(optional)', 'wp-event-manager' ) . '</small>' : '' ); ?></label>				<div class="field">					<input type="text" class="input-text" name="create_account_username" id="account_username" value="<?php echo empty( $_POST['create_account_username'] ) ? '' : esc_attr( sanitize_text_field( stripslashes( $_POST['create_account_username'] ) ) ); ?>" />				</div>			</fieldset>		<?php endif; ?>		<fieldset>			<label><?php _e( 'Your email', 'wp-event-manager' ); ?> <?php echo apply_filters( 'submit_event_form_required_label', ( ! $account_required ) ? ' <small>' . __( '(optional)', 'wp-event-manager' ) . '</small>' : '' ); ?></label>			<div class="field">				<input type="email" class="input-text" name="create_account_email" id="account_email" placeholder="<?php esc_attr_e( 'you@yourdomain.com', 'wp-event-manager' ); ?>" value="<?php echo empty( $_POST['create_account_email'] ) ? '' : esc_attr( sanitize_text_field( stripslashes( $_POST['create_account_email'] ) ) ); ?>" />			</div>		</fieldset>		<?php do_action( 'event_manager_register_form' ); ?>	<?php endif; ?><?php endif; ?>